import { Route, Routes, Navigate, Link, useParams } from "react-router-dom";
import AllQuotes from "./pages/AllQuotes";
import NewQuote from "./pages/NewQuote";
import Layout from "./components/layout/Layout";
import Comments from "./components/comments/Comments";
import NotFound from "./pages/NotFound";
import { Fragment } from "react";
import HighlightedQuote from './components/quotes/HighlightedQuote'

const DUMMY_QUOTES=[
  {id:'q1', author:'Max',text:'Learning Rect is fun!'},
  {id:'q2', author:'Rohit',text:'Learning Rect is greate!'},
]

function App() {
  return (
    <Layout>
      <Routes>
        <Route path="/" element={<Navigate replace to="/quotes" />} />
        <Route path="/quotes" element={<AllQuotes />} />
        <Route path="/quotes/:quoteId" element={<QuoteDetailWithComments />} />
        <Route path="/new-quote" element={<NewQuote />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
    </Layout>
  );
}

function QuoteDetailWithComments() {
  const params = useParams();
  const quote = DUMMY_QUOTES.find(quote=> quote.id === params.quoteId);

  return (
    <Fragment>
      <HighlightedQuote text={quote.text} author={quote.author} />
      <div className="centered">
        <Link className="btn--flat" to={`/quotes/${params.quoteId}/comment`}>
          Load Comments
        </Link>
        <Route path="/quotes/:quoteId/comment" element={<Comments />} />
      </div>
    </Fragment>
  );
}

export default App;
