import { Route, Routes, Navigate,Link,useParams } from "react-router-dom";
import AllQuotes from "./pages/AllQuotes";
import QuoteDetail from "./pages/QuoteDetail";
import NewQuote from "./pages/NewQuote";
import Layout from "./components/layout/Layout";
import Comments from "./components/comments/Comments";
import NotFound from "./pages/NotFound";

function App() {
  const params = useParams();
  console.log(params.quoteId)

  return (
    <Layout>
      <Routes>
        <Route path="/" element={<Navigate replace to="/quotes" />} />
        <Route path="/quotes" element={<AllQuotes />} />
        <Route path="/quotes/:quoteId" element={<QuoteDetail/>}/>
        <Route path="/quotes/:quoteId" element={<QuoteDetailWithComments />}/>
        <Route path="/new-quote" element={<NewQuote />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
    </Layout>
  );
}

function QuoteDetailWithComments() {
  const params = useParams();

  return (
    <div className="centered">
      <Link className="btn--flat" to={`/quotes/${params.quoteId}/comment`}>
        Load Comments
      </Link>
      <Route path="/quotes/:quoteId/comment" element={<Comments />} />
    </div>
  );
}

export default App;
