import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
  },
  reducers: {
    addItemToCart(state, action) {
      const newItem = action.payload;
      const existingItem = state.items.find((item) => item.id === newItem.id);
      state.totalQuantity++;
      if (!existingItem) {
        state.items.push({
          id: newItem.id,
          price: newItem.price,
          quantity: 1,
          totalPrice: newItem.price,
          name:newItem.title
        });
      }
      else{
        existingItem.quantity++;
        existingItem.totalPrice = existingItem.totalPrice + newItem.price
      }
    },
    removeItemToCart(state,action) {
        const existitem = action.payload;
        const existingItem = state.items.find((item) => item.id === existitem.id); 
        state.totalQuantity--;
        if(existingItem.quantity === 1){
            state.items = state.items.filter(item => item.id !== existitem.id);
        }else{
            existingItem.quantity--;
            existingItem.totalPrice = existingItem.totalPrice - existitem.price;
        }
    },
  },
});


export const cartActions = cartSlice.actions;

export default cartSlice;
