import { useState } from "react";

const SimpleInput = (props) => {
  // const nameInputRef = useRef();
  const [enteredName,setEnteredName] = useState('')
  const [enteredNameIsValid,setEnteredNameIsValid] = useState(true);

  const nameInputChangedHandler = (event)=>{
    setEnteredName(event.target.value);
  }

  const formSubmitHandler = (event) =>{
    event.preventDefault();

    if(enteredName.trim() === ''){
      setEnteredNameIsValid(false);
      return;
    }
    setEnteredNameIsValid(true);

    console.log(enteredName);
    // const enteredValue = nameInputRef.current.value;
    // console.log(enteredValue);

    // nameInputRef.current.value = '' => don't manipulte the dom , not ideal
    setEnteredName('');
  }

  const nameInputClass = enteredNameIsValid ? 'form-control' : 'form-control invalid';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClass}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameInputChangedHandler} value={enteredName}/>
        {!enteredNameIsValid && <p className="error-text">Name must not be empty</p>}
        </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
